class MyPage : System.Web.UI.Page
{
   private bool FieldExists(string checkName)
   {
       DataSet dataSet = Product.GetProductDataSet();
       foreach (DataColumn dc in dataSet.Tables[0].Columns)
       {
           if (checkName == dc.ColumnName)
           {
               return true;
           }
       }
       return false;
   }
}

